import { Box, Button, TextField, Typography, styled } from '@mui/material';

const Component = styled(Box)`
    width: 400px;
    margin: auto;
    box-shadow: 5px 2px 5px 2px rgb(0 0 0/ 0.6);
`;

const Image = styled('img')({
  width: 100,
  display: 'flex',
  margin: 'auto',
  padding: '50px 0 0'
});

const Wrapper = styled(Box)`
    padding: 25px 35px;
    display: flex;
    flex: 1;
    overflow: auto;
    flex-direction: column;
    & > div, & > button, & > p {
        margin-top: 20px;
    }
`;

const LoginButton = styled(Button)`
    text-transform: none;
    background: #FB641B;
    color: #fff;
    height: 48px;
    border-radius: 2px;
`;

const SignupButton = styled(Button)`
    text-transform: none;
    background: #fff;
    color: #2874f0;
    height: 48px;
    border-radius: 2px;
    box-shadow: 0 2px 4px 0 rgb(0 0 0 / 20%);
`;

// const Text = styled(Typography)`
//     color: #878787;
//     font-size: 12px;
// `;

// const Error = styled(Typography)`
//     font-size: 10px;
//     color: #ff6161;
//     line-height: 0;
//     margin-top: 10px;
//     font-weight: 600;
// `


const Login = () => {

  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt='login' />
        <Wrapper>
          <TextField type="text" variant="standard" label="Enter Name" />
          <TextField type="password" variant="standard" label="Enter Password" />
          <LoginButton variant='contained'>Login</LoginButton>
          <Typography style={{ textAlign: 'center' }}>OR</Typography>
          <SignupButton>Create an account</SignupButton>
        </Wrapper>
        <Wrapper>
          <TextField variant="standard" onChange={(e) => onInputChange(e)} name='name' label='Enter Name' />
          <TextField variant="standard" onChange={(e) => onInputChange(e)} name='username' label='Enter Username' />
          <TextField variant="standard" onChange={(e) => onInputChange(e)} name='password' label='Enter Password' />

          <SignupButton  >Signup</SignupButton>
          <Text style={{ textAlign: 'center' }}>OR</Text>
          <LoginButton variant="contained">Already have an account</LoginButton>
        </Wrapper>
      </Box>
    </Component>
  )
}

export default Login
